const equalHeight=e=>{const t=document.querySelectorAll(e);let l,h=0;t.forEach((e=>{e.style.setProperty("height","unset")})),t.forEach((e=>{l=e.clientHeight,l>h&&(h=l)})),t.forEach((e=>{e.style.setProperty("height",h+"px")}))};equalHeight(".element"),window.addEventListener("resize",(()=>{equalHeight(".element")}));